function findHardcodedSysIDs(code) {
    var pattern = /("|'|=|h|H|n|N|f|F|,)[a-z0-9]{32}("|'|\^|,|@)/gm;
    var matches = [];

    // Split the code into individual lines or chunks to simulate node traversal
    var lines = code.split('\n');

    lines.forEach(function(line, index) {
        // Check each line against the sys_id pattern
        if (pattern.test(line)) {
            matches.push({
                line: index + 1,
                content: line.trim()
            });
        }
    });

    if (matches.length > 0) {
        gs.print("Hardcoded sys_ids found at:");
        matches.forEach(function(match) {
            gs.print("Line " + match.line + ": " + match.content);
        });
    } else {
        gs.print("No hardcoded sys_ids found.");
    }

    return matches;
}
