scan_script_only_check.script:
(function(finding) {

    var grSysReport = new GlideRecord('sys_report');
    grSysReport.addEncodedQuery("roles=public");
    grSysReport.query();
    while (grSysReport.next()) {
        finding.setCurrentSource(grSysReport);
        finding.increment();
    }

})(finding);
