scan_script_only_check.script:
(function(finding) {

    var grSysauto = new GlideRecord('sysauto');
    grSysauto.addEncodedQuery("run_as.locked_out=true");
    grSysauto.query();
    while (grSysauto.next()) {
        finding.setCurrentSource(grSysauto);
        finding.increment();
    }

})(finding);
