scan_table_check.script:
(function(finding, current) {
    var IncID = current.getValue('sys_id');
    var inc = new GlideRecord('incident');
    inc.addQuery('sys_id', IncID);
    inc.addEncodedQuery("assignment_group!=null");
    if (!inc.hasNext()) {
        finding.increment();
    }
})(finding, current);
