scan_table_check.script:
(function (engine) {

    var gr_acl = new GlideRecord('sys_security_acl');
		gr_acl.addQuery('type','ui_page');
		gr_acl.addQuery('name','CONTAINS',current.name);
		gr_acl.query();
		if(!gr_acl.next()){
			finding.setCurrentSource(current);
			finding.increment();
		}
})(engine);
